package com.test.imagedemo;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import com.myzaker.imagescan.ShowImageActivity;import com.myzaker.imagescan.Imageload.BitmapCache;import com.myzaker.imagescan.Imageload.LocalImageView;import com.myzaker.imagescan.bean.ImageBean;public class SelectImageGridAdpater extends BaseAdapter {	List<ImageBean> imagesData;	Context context;	LayoutInflater mInflater;	public SelectImageGridAdpater(Context context, List<ImageBean> imagesData) {		this.context = context;		this.imagesData = imagesData;		mInflater = LayoutInflater.from(context);	}	@Override	public int getCount() {		if (imagesData != null) {			return imagesData.size();		}		return 0;	}	public void setImageDatas(List<ImageBean> imagesData) {		this.imagesData = imagesData;	}	@Override	public Object getItem(int position) {		if (imagesData != null) {			return imagesData.get(position);		}		return null;	}	@Override	public long getItemId(int position) {		return 0;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		final ViewHolder viewHolder;		final ImageBean currentImageBean = imagesData.get(position);		convertView = mInflater.inflate(R.layout.image_child_item, parent,				false);		viewHolder = new ViewHolder();		viewHolder.mImageView = (LocalImageView) convertView				.findViewById(R.id.child_image);		bindData(currentImageBean, viewHolder);		return convertView;	}	protected void bindData(ImageBean mImageBean, ViewHolder viewHolder) {		String path = mImageBean.getImagePath();		viewHolder.mImageView				.setDefaultImageResId(ShowImageActivity.mSkinUtil.gridItemDefaultRes);		viewHolder.mImageView.setImageUrl("file://" + path,				BitmapCache.getImageLoader());	}	public static class ViewHolder {		public LocalImageView mImageView;	}}