package com.myzaker.imagescan.util;import com.myzaker.imagescan.R;import android.content.Intent;import android.graphics.Color;public class SkinUtil {	public final static String KEY_HEADERBAR_BG_COLOR = "KEY_HEADERBAR_BG_COLOR";	public final static String KEY_HEADERBAR_TEXT_COLOR = "KEY_HEADERBAR_TEXT_COLOR";	public final static String KEY_HEADERBAR_FINISH_RES = "KEY_HEADERBAR_FINISH_RES";	public final static String KEY_FOOTERBAR_BG_COLOR = "KEY_FOOTERBAR_BG_COLOR";	public final static String KEY_FOOTERBAR_DIVIDER_COLOR = "KEY_FOOTERBAR_DIVIDER_COLOR";	public final static String KEY_CONTENT_BG_COLOR = "KEY_CONTENT_BG_COLOR";	public final static String KEY_GRID_ITEM_DEFAULT_RES = "KEY_GRID_ITEM_DEFAULT_RES";	public final static String KEY_GRID_ITEM_SELECT_COLOR = "KEY_GRID_ITEM_SELECT_COLOR";	public final static String KEY_GRID_SELECT_RES = "KEY_GRID_SELECT_RES";	public final static String KEY_PREVIEW_BOTTOM_TEXT_COLOR = "KEY_PREVIEW_BOTTOM_TEXT_COLOR";	public final static String KEY_PREVIEW_SELECT_RES = "KEY_PREVIEW_SELECT_RES";	public final static String KEY_PREVIEW_UNSELECT_RES = "KEY_PREVIEW_UNSELECT_RES";	public final static String KEY_PREVIEW_MASK_COLOR = "KEY_PREVIEW_MASK_COLOR";	public final static String KEY_SELECTPIC_BOTTOM_TEXT_COLOR = "KEY_SELECTPIC_BOTTOM_TEXT_COLOR";	public final static String KEY_SELECTPIC_BOTTOM_TEXT2_COLOR = "KEY_SELECTPIC_BOTTOM_TEXT2_COLOR";	public final static String KEY_SELECTPIC_BOTTOM_TEXT_ICON_RES = "KEY_SELECTPIC_BOTTOM_TEXT_ICON_RES";	public final static String KEY_FOLDER_TEXT_COLOR = "KEY_FOLDER_TEXT_COLOR";	public final static String KEY_FOLDER_DIVIDER_COLOR = "KEY_FOLDER_DIVIDER_COLOR";	public final static String KEY_FOLDER_SUBTEXT_COLOR = "KEY_FOLDER_SUBTEXT_COLOR";	public final static String KEY_FOLDER_SELECTTEXT_COLOR = "KEY_FOLDER_SELECTTEXT_COLOR";	public final static String KEY_CAMERA_ICON_RES = "KEY_CAMERA_ICON_RES";	public int headerBarBgColor;	public int headerBarTextColor;	public int headerBarFinishRes;	public int contentBgColor;	public int gridItemSelectColor;	public int gridItemDefaultRes;	public int gridItemSelectRes;	public int footerBarBgColor;	public int footerBarDividerColor;	public int folderTextColor;	public int folderSubTextColor;	public int folderSelectTextColor;	public int folderDividerColor;	public int previewBottomTextColor;	public int previewSelectRes;	public int previewUnselectRes;	public int previewMaskColor;	public int selectPicBottomTextIconRes;	public int selectPicBottomTextColor;	public int selectPicBottomTextColor2;	public int cameraIconRes;	public SkinUtil() {		headerBarBgColor = 0xff00B4FD;		headerBarTextColor = 0xffffffff;		headerBarFinishRes = R.drawable.botton_finish_color;		contentBgColor = Color.WHITE;		gridItemSelectColor = 0x99046cd6;		gridItemDefaultRes = R.drawable.default_board_img;		previewBottomTextColor = 0xffbfbfbf;		previewSelectRes = R.drawable.yeschoose;		previewUnselectRes = R.drawable.nochoose;		selectPicBottomTextColor = 0xff848484;		selectPicBottomTextColor2 = 0xff00b4fd;		cameraIconRes = R.drawable.selector_share_camera;		selectPicBottomTextIconRes = R.drawable.arrow;		previewMaskColor = 0xc8ffffff;		folderTextColor = Color.BLACK;		folderDividerColor = 0x31B99FFF;		gridItemSelectRes = R.drawable.magnifier;		folderSubTextColor = Color.BLACK;		folderSelectTextColor = 0xff00B4FD;		footerBarBgColor = Color.WHITE;		footerBarDividerColor = 0xff00B4FD;	}	public SkinUtil(Intent mIntent) {		this();		headerBarBgColor = mIntent.getIntExtra(KEY_HEADERBAR_BG_COLOR,				headerBarBgColor);		headerBarTextColor = mIntent.getIntExtra(KEY_HEADERBAR_TEXT_COLOR,				headerBarTextColor);		headerBarFinishRes = mIntent.getIntExtra(KEY_HEADERBAR_FINISH_RES,				headerBarFinishRes);		contentBgColor = mIntent.getIntExtra(KEY_CONTENT_BG_COLOR,				contentBgColor);		gridItemSelectColor = mIntent.getIntExtra(KEY_GRID_ITEM_DEFAULT_RES,				gridItemSelectColor);		gridItemDefaultRes = mIntent.getIntExtra(KEY_GRID_ITEM_SELECT_COLOR,				gridItemDefaultRes);		previewBottomTextColor = mIntent.getIntExtra(				KEY_PREVIEW_BOTTOM_TEXT_COLOR, previewBottomTextColor);		previewSelectRes = mIntent.getIntExtra(KEY_PREVIEW_SELECT_RES,				previewSelectRes);		previewUnselectRes = mIntent.getIntExtra(KEY_PREVIEW_UNSELECT_RES,				previewUnselectRes);				selectPicBottomTextColor = mIntent.getIntExtra(				KEY_SELECTPIC_BOTTOM_TEXT_COLOR, selectPicBottomTextColor);				selectPicBottomTextColor2 = mIntent.getIntExtra(				KEY_SELECTPIC_BOTTOM_TEXT2_COLOR, selectPicBottomTextColor2);				cameraIconRes = mIntent.getIntExtra(KEY_CAMERA_ICON_RES, cameraIconRes);				selectPicBottomTextIconRes = mIntent.getIntExtra(				KEY_SELECTPIC_BOTTOM_TEXT_ICON_RES, selectPicBottomTextIconRes);		previewMaskColor = mIntent.getIntExtra(KEY_PREVIEW_MASK_COLOR,				previewMaskColor);		folderTextColor = mIntent.getIntExtra(KEY_FOLDER_TEXT_COLOR,				folderTextColor);		folderDividerColor = mIntent.getIntExtra(KEY_FOLDER_DIVIDER_COLOR,				folderDividerColor);		gridItemSelectRes = mIntent.getIntExtra(KEY_GRID_SELECT_RES,				gridItemSelectRes);		folderSubTextColor = mIntent.getIntExtra(KEY_FOLDER_SUBTEXT_COLOR,				folderSubTextColor);		folderSelectTextColor = mIntent.getIntExtra(				KEY_FOLDER_SELECTTEXT_COLOR, folderSelectTextColor);		footerBarBgColor = mIntent.getIntExtra(KEY_FOOTERBAR_BG_COLOR,				footerBarBgColor);		footerBarDividerColor = mIntent.getIntExtra(				KEY_FOOTERBAR_DIVIDER_COLOR, footerBarDividerColor);	}}